mapper.insert:
 INSERT INTO member (   user_id
					       , pwd
					       , name
					       , nickname
					       , email )
               VALUES(    
              		    #{id}
              		   , #{pwd}
              		   , #{name}
              		   , #{nickname}
              		   , #{email}
              		   )

mapper.select:
	 SELECT id
       FROM member
      WHERE id = #{id}
